CREATE TABLE ccda_components (  ccda_components_id int(11) NOT NULL AUTO_INCREMENT,  ccda_components_field varchar(100) DEFAULT NULL,  ccda_components_name varchar(100) DEFAULT NULL,  PRIMARY KEY (ccda_components_id)); CREATE TABLE ccda_sections (  ccda_sections_id int(11) NOT NULL AUTO_INCREMENT,  ccda_components_id int(11) DEFAULT NULL,  ccda_sections_field varchar(100) DEFAULT NULL,  ccda_sections_name varchar(100) DEFAULT NULL,  ccda_sections_req_mapping tinyint(4) NOT NULL DEFAULT '1',  PRIMARY KEY (ccda_sections_id)); CREATE TABLE ccda_table_mapping (  id int(11) NOT NULL AUTO_INCREMENT,  ccda_component varchar(100) DEFAULT NULL,  ccda_component_section varchar(100) DEFAULT NULL,  form_dir varchar(100) DEFAULT NULL,  form_type smallint(6) DEFAULT NULL,  form_table varchar(100) DEFAULT NULL,  user_id int(11) DEFAULT NULL,  deleted tinyint(4) NOT NULL DEFAULT '0',  timestamp timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (id));CREATE TABLE ccda_field_mapping (  id int(11) NOT NULL AUTO_INCREMENT,  table_id int(11) DEFAULT NULL,  ccda_field varchar(100) DEFAULT NULL,  PRIMARY KEY (id));CREATE TABLE ccda (  id INT(11) NOT NULL AUTO_INCREMENT,  pid BIGINT(20) DEFAULT NULL,  encounter BIGINT(20) DEFAULT NULL,  ccda_data MEDIUMTEXT,  time VARCHAR(50) DEFAULT NULL,  status SMALLINT(6) DEFAULT NULL,  updated_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (id),  UNIQUE KEY Unique (pid,encounter,time));